mapper.select:
SELECT order_id AS orderId, order_history AS orderHistory, sample_id AS sampleId, sample_type AS sampleType, sample_position AS samplePosition, primer, primer_type AS primerType, primer_position AS primerPosition, primer_concentration AS primerConcentration, seq, project, carrier_name AS carrierName, antibiotic_type AS antibioticType, plasmid_length AS plasmidLength, fragment_size AS fragmentSize, test_result AS testResult, origin_concentration AS originConcentration, template_plate_no AS templatePlateNo, template_hole_no AS templateHoleNo, performance, return_state AS returnState, flow_name AS flowName, plate_no AS plateNo, hole_no AS holeNo, belong_company AS belongCompany, produce_company AS produceCompany, hole_number AS holeNumber, layout, create_user AS createUser, create_time AS createTime, remark FROM yk_sample_info
AND order_id LIKE CONCAT('%', #{query.orderId}, '%')
AND order_history LIKE CONCAT('%', #{query.orderHistory}, '%')
AND sample_id LIKE CONCAT('%', #{query.sampleId}, '%')
AND sample_type = #{query.sampleType}
AND primer LIKE CONCAT('%', #{query.primer}, '%')
AND primer_type = #{query.primerType}
AND project = #{query.project}
AND carrier_name LIKE CONCAT('%', #{query.carrierName}, '%')
AND antibiotic_type = #{query.antibioticType}
AND test_result = #{query.testResult}
AND performance = #{query.performance}
AND return_state = #{query.returnState}
AND flow_name LIKE CONCAT('%', #{query.flowName}, '%')
AND plate_no LIKE CONCAT('%', #{query.plateNo}, '%')
AND belong_company LIKE CONCAT('%', #{query.belongCompany}, '%')
AND produce_company LIKE CONCAT('%', #{query.produceCompany}, '%')
AND create_user LIKE CONCAT('%', #{query.createUser}, '%')
ORDER BY create_time DESC

